
CREATE TABLE zz_exp_bandalarga
(
    codice_fiscale_ente character varying(255) COLLATE pg_catalog."default",
    descrizione_ente character varying(255) COLLATE pg_catalog."default",
	flag_amm_procedente  int,
    pec_ente character varying(255) COLLATE pg_catalog."default"
);


 commit;
	


select 		
nextval('ente_id_ente_seq'),
codice_fiscale_ente ,
descrizione_ente ,
case when flag_amm_procedente=0 THEN FALSE else TRUE END ,
pec_ente
from zz_exp_bandalarga ia
WHERE 1=1
);
commit;




